mapper.select:
select * from retailer

    and name like #{name}
    and address like #{address}
    and telphone = #{telphone}

        and create_time = DATE_FORMAT(#{createTime}, '%Y-%m-%d %H:%i:%s')


        and create_time >= DATE_FORMAT(#{startTime},'%Y-%m-%d %H:%i:%s')


        and create_time <= DATE_FORMAT(#{endTime},'%Y-%m-%d %H:%i:%s')

    limit #{startPage}, #{PageSize}

select count(*) from retailer

    and name like #{name}
    and address like #{address}
    and telphone = #{telphone}

        and create_time = DATE_FORMAT(#{createTime}, '%Y-%m-%d %H:%i:%s')


        and create_time >= DATE_FORMAT(#{startTime},'%Y-%m-%d %H:%i:%s')


        and create_time <= DATE_FORMAT(#{endTime},'%Y-%m-%d %H:%i:%s')

mapper.update:
update retailer

    retailer_id = #{retailerId,jdbcType=VARCHAR}
    name = #{name,jdbcType=VARCHAR}
    telphone = #{telphone,jdbcType=VARCHAR}
    status = #{status,jdbcType=INT}
    create_Time = #{createTime,jdbcType=VARCHAR}
    where retailer_id = #{retailerId}
